mapper.select:
select id, user_id, entity_type, entity_id, target_id, content, status, create_time
from `comment`
where status = 0
and entity_type = #{entityType}
and entity_id = #{entityId}
order by create_time asc
limit #{offset}, #{limit}

select count(id)
from `comment`
where status = 0
  and entity_type = #{entityType}
  and entity_id = #{entityId}

select id, user_id, entity_type, entity_id, target_id, content, status, create_time
from comment
where id = #{id}
